import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import socket from '../utils/socket';
import Sidebar from './Sidebar';
import './Orders.css';

const Orders = ({ onLogout }) => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        const socket = io('http://localhost:3000', {
            auth: { token: localStorage.getItem('token') },
        });
    
        // Confirm socket connection
        socket.on('connect', () => {
            console.log('Socket connected:', socket.id);
        });
    
        // Fetch initial orders
        const fetchOrders = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/orders', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                if (!response.ok) {
                    throw new Error(`Error fetching orders: ${response.statusText}`);
                }
                const data = await response.json();
                setOrders(data);
                console.log('Fetched initial orders:', data); // Debugging
            } catch (error) {
                console.error('Error fetching orders:', error);
            }
        };
    
        fetchOrders();
    
        // Listen for real-time updates
        socket.on('ordersUpdate', (updatedOrders) => {
            console.log('Received orders update:', updatedOrders); // Debugging
            setOrders(updatedOrders); // Update state with the new data
        });
    
        // Cleanup function
        return () => {
            socket.disconnect();
        };
    }, []);

    return (
        <div style={{ display: 'flex' }}>
            <Sidebar onLogout={onLogout} />
            <div className="orders-content">
                <h1>Orders</h1>
                <table className="orders-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Customer Name</th>
                            <th>Invoice Status</th>
                            <th>Invoice Number</th>
                            <th>Created By</th>
                            <th>Created At</th>
                            <th>Updated At</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map((order) => (
                            <tr key={order.id}>
                                <td>{order.id}</td>
                                <td>{order.customer_name}</td>
                                <td>{order.invoice_status}</td>
                                <td>{order.invoice_number}</td>
                                <td>{order.created_by}</td>
                                <td>{new Date(order.created_at).toLocaleString()}</td>
                                <td>{new Date(order.updated_at).toLocaleString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Orders;
