import React, { useState, useEffect } from 'react';
import socket from '../socket'; // Import the socket instance
import './Orders.css';

const Orders = () => {
    const [orders, setOrders] = useState([]);
    const [showModal, setShowModal] = useState(false); // To show or hide the modal
    const [formData, setFormData] = useState({
        customer_name: '',
        invoice_status: '',
        invoice_number: '',
        created_by: '',
    });

    useEffect(() => {
        // Fetch initial orders from backend
        const fetchOrders = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/orders', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                if (!response.ok) {
                    throw new Error(`Error fetching orders: ${response.statusText}`);
                }
                const data = await response.json();
                setOrders(data);
                console.log('Fetched initial orders:', data);
            } catch (error) {
                console.error('Error fetching orders:', error);
            }
        };

        fetchOrders();

        // Listen for real-time updates
        socket.on('ordersUpdate', (updatedOrders) => {
            console.log('Received order update:', updatedOrders);
            setOrders(updatedOrders);
        });

        return () => {
            socket.off('ordersUpdate');
        };
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:3000/api/orders', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                throw new Error('Failed to create order');
            }

            setShowModal(false); // Close the modal after success
            setFormData({
                customer_name: '',
                invoice_status: '',
                invoice_number: '',
                created_by: '',
            });
        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

    return (
        <div className="orders-container">
            <h2>Orders</h2>
            <button className="add-order-button" onClick={() => setShowModal(true)}>
                Add Order
            </button>
            <table className="orders-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Customer Name</th>
                        <th>Invoice Status</th>
                        <th>Invoice Number</th>
                        <th>Created By</th>
                        <th>Created At</th>
                        <th>Updated At</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order) => (
                        <tr key={order.id}>
                            <td>{order.id}</td>
                            <td>{order.customer_name}</td>
                            <td>{order.invoice_status}</td>
                            <td>{order.invoice_number}</td>
                            <td>{order.created_by}</td>
                            <td>{new Date(order.created_at).toLocaleString()}</td>
                            <td>{new Date(order.updated_at).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {showModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Add New Order</h3>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>Customer Name</label>
                                <input
                                    type="text"
                                    name="customer_name"
                                    value={formData.customer_name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Invoice Status</label>
                                <input
                                    type="text"
                                    name="invoice_status"
                                    value={formData.invoice_status}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Invoice Number</label>
                                <input
                                    type="text"
                                    name="invoice_number"
                                    value={formData.invoice_number}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Created By</label>
                                <input
                                    type="text"
                                    name="created_by"
                                    value={formData.created_by}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="modal-actions">
                                <button type="button" onClick={() => setShowModal(false)}>
                                    Cancel
                                </button>
                                <button type="submit">Submit</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Orders;
