import { io } from 'socket.io-client';

        const socket = io('http://localhost:3000', {
            auth: { token: localStorage.getItem('token') },
        });
    
        // Confirm socket connection
        socket.on('connect', () => {
            console.log('Socket connected:', socket.id);
        });

export default socket;
