import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const RealTimeData = ({ onLogout }) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem('token'); // Get the token from localStorage

        const socket = io('http://localhost:3000', {
            auth: { token }, // Send the token during the connection
        });

        socket.on('billboardUpdate', (updatedProducts) => {
            console.log('Received billboard update:', updatedProducts);
            setProducts(updatedProducts);
        });

        return () => {
            socket.disconnect(); // Cleanup socket connection
        };
    }, []);

    return (
        <div>
            <h2>Real-Time Data</h2>
            {/* Add your table or other UI here */}
        </div>
    );
};

export default RealTimeData;
