import React, { useEffect, useState } from 'react';
import socket from '../utils/socket'; // Shared socket instance
import './Dashboard.css';

const Dashboard = ({ onLogout }) => {
    const [analytics, setAnalytics] = useState({
        totalBillboards: 0,
        totalOrders: 0,
        totalReservations: 0,
        totalUsers: 0,
    });

    useEffect(() => {
        // Function to fetch initial analytics
        const fetchAnalytics = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/analytics', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                if (!response.ok) {
                    throw new Error(`Error fetching analytics: ${response.statusText}`);
                }
                const data = await response.json();
                setAnalytics(data);
                console.log('Fetched initial analytics:', data); // Debugging log
            } catch (error) {
                console.error('Error fetching analytics:', error);
            }
        };

        fetchAnalytics();

        // Listen for real-time analytics updates
        socket.on('dashboardAnalyticsUpdate', (updatedAnalytics) => {
            console.log('Received dashboard analytics update:', updatedAnalytics); // Debugging log
            setAnalytics(updatedAnalytics);
        });

        // Cleanup on unmount
        return () => {
            socket.off('dashboardAnalyticsUpdate'); // Remove listener
        };
    }, []);

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h2>Dashboard</h2>
                <button className="logout-button" onClick={onLogout}>
                    Logout
                </button>
            </div>
            <div className="dashboard-cards">
                <div className="dashboard-card">
                    <h3>Total Billboards</h3>
                    <p>{analytics.totalBillboards}</p>
                </div>
                <div className="dashboard-card">
                    <h3>Total Orders</h3>
                    <p>{analytics.totalOrders}</p>
                </div>
                <div className="dashboard-card">
                    <h3>Total Reservations</h3>
                    <p>{analytics.totalReservations}</p>
                </div>
                <div className="dashboard-card">
                    <h3>Total Users</h3>
                    <p>{analytics.totalUsers}</p>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
