import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const RealTimeData = ({ onLogout }) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Retrieve token from localStorage
        const token = localStorage.getItem('token');
        if (!token) {
            console.error('No token found! Redirecting to login...');
            onLogout(); // Log out if no token exists
            return;
        }

        // Connect to the Socket.IO server with the token
        const socket = io('http://localhost:3000', {
            auth: { token }, // Send the token as part of the handshake
        });

        // Handle successful connection
        socket.on('connect', () => {
            console.log('Connected to WebSocket server:', socket.id);
        });

        // Handle stock updates
        socket.on('stockUpdate', (updatedProducts) => {
            console.log('Received stock update:', updatedProducts);
            setProducts(updatedProducts);
        });

        // Handle connection errors
        socket.on('connect_error', (err) => {
            console.error('Socket connection error:', err.message);
            onLogout(); // Log out if token validation fails
        });

        // Cleanup on component unmount
        return () => {
            socket.disconnect();
        };
    }, [onLogout]);

    return (
        <div style={{ maxWidth: '600px', margin: '50px auto' }}>
            <h2>Real-Time Stock Data</h2>
            <button
                style={{
                    marginBottom: '20px',
                    padding: '10px 15px',
                    backgroundColor: '#f44336',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
                onClick={onLogout}
            >
                Logout
            </button>
            <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Product</th>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product) => (
                            <tr key={product.id}>
                                <td style={{ border: '1px solid black', padding: '8px' }}>{product.name}</td>
                                <td style={{ border: '1px solid black', padding: '8px' }}>{product.stock}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td style={{ border: '1px solid black', padding: '8px' }} colSpan="2">
                                No products available.
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default RealTimeData;
