import React, { useState, useEffect } from 'react';
import socket from '../utils/socket'; // Centralized socket instance
import Sidebar from './Sidebar';
import './Orders.css';

const Orders = () => {
    const [orders, setOrders] = useState([]);
    const [showModal, setShowModal] = useState(false); // Modal visibility state
    const [newOrder, setNewOrder] = useState({
        customer_name: '',
        invoice_status: '',
        invoice_number: '',
        created_by: '',
    }); // New order data

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/orders', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
                });
                if (!response.ok) {
                    throw new Error(`Error fetching orders: ${response.statusText}`);
                }
                const data = await response.json();
                setOrders(data);
                console.log('Fetched initial orders:', data);
            } catch (error) {
                console.error('Error fetching orders:', error);
            }
        };

        fetchOrders();

        // Listen for real-time updates
        socket.on('ordersUpdate', (updatedOrders) => {
            console.log('Received orders update:', updatedOrders);
            setOrders(updatedOrders);
        });

        return () => {
            socket.off('ordersUpdate'); // Clean up the listener
        };
    }, []);

    const handleAddOrder = () => {
        setShowModal(true); // Show the modal
    };

    const handleCloseModal = () => {
        setShowModal(false); // Hide the modal
        setNewOrder({
            customer_name: '',
            invoice_status: '',
            invoice_number: '',
            created_by: '',
        }); // Reset form data
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewOrder((prevOrder) => ({
            ...prevOrder,
            [name]: value,
        }));
    };

    const handleConfirmAddOrder = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/orders', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
                body: JSON.stringify(newOrder),
            });
            if (!response.ok) {
                throw new Error(`Error adding order: ${response.statusText}`);
            }
            console.log('Order added successfully');
            handleCloseModal(); // Close the modal after successful addition
        } catch (error) {
            console.error('Error adding order:', error);
        }
    };

    return (
        <div className="orders-content">
            <h1>Orders</h1>
            <button className="add-order-button" onClick={handleAddOrder}>
                Add Order
            </button>
            <table className="orders-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Customer Name</th>
                        <th>Invoice Status</th>
                        <th>Invoice Number</th>
                        <th>Created By</th>
                        <th>Created At</th>
                        <th>Updated At</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.length > 0 ? (
                        orders.map((order) => (
                            <tr key={order.id}>
                                <td>{order.id}</td>
                                <td>{order.customer_name}</td>
                                <td>{order.invoice_status}</td>
                                <td>{order.invoice_number}</td>
                                <td>{order.created_by}</td>
                                <td>{new Date(order.created_at).toLocaleString()}</td>
                                <td>{new Date(order.updated_at).toLocaleString()}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="7">No orders found.</td>
                        </tr>
                    )}
                </tbody>
            </table>

            {showModal && (
                <div className="modal-overlay">
                    <div className="modal">
                        <h2>Add New Order</h2>
                        <div className="form-group">
                            <label>Customer Name</label>
                            <input
                                type="text"
                                name="customer_name"
                                value={newOrder.customer_name}
                                onChange={handleInputChange}
                            />
                        </div>
                        <div className="form-group">
                            <label>Invoice Status</label>
                            <select
                                name="invoice_status"
                                value={newOrder.invoice_status}
                                onChange={handleInputChange}
                            >
                                <option value="">Select Status</option>
                                <option value="Paid">Paid</option>
                                <option value="Unpaid">Unpaid</option>
                            </select>
                        </div>
                        <div className="form-group">
                            <label>Invoice Number</label>
                            <input
                                type="text"
                                name="invoice_number"
                                value={newOrder.invoice_number}
                                onChange={handleInputChange}
                            />
                        </div>
                        <div className="form-group">
                            <label>Created By</label>
                            <input
                                type="text"
                                name="created_by"
                                value={newOrder.created_by}
                                onChange={handleInputChange}
                            />
                        </div>
                        <div className="modal-actions">
                            <button className="confirm-button" onClick={handleConfirmAddOrder}>
                                Confirm
                            </button>
                            <button className="cancel-button" onClick={handleCloseModal}>
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Orders;
