import React, { useState, useEffect } from "react";
import "./Orders.css";
import socket from "../socket";

const Orders = ({ userName }) => {
  const [orders, setOrders] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [newOrder, setNewOrder] = useState({
    customerName: "",
    invoiceStatus: "Paid",
    invoiceNumber: "",
    createdBy: userName || "",
  });

  useEffect(() => {
    // Fetch orders and listen for updates
    const fetchOrders = async () => {
      try {
        const response = await fetch("http://localhost:3000/api/orders", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const data = await response.json();
        setOrders(data);
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };

    fetchOrders();

    socket.on("orderUpdate", (updatedOrders) => {
      setOrders(updatedOrders);
    });

    return () => {
      socket.off("orderUpdate");
    };
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrder((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:3000/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify(newOrder),
      });

      if (response.ok) {
        setShowPopup(false);
        setNewOrder({
          customerName: "",
          invoiceStatus: "Paid",
          invoiceNumber: "",
          createdBy: userName || "",
        });
      }
    } catch (error) {
      console.error("Error adding new order:", error);
    }
  };

  return (
    <div className="orders-container">
      <h2>Orders</h2>
      <button className="add-order-button" onClick={() => setShowPopup(true)}>
        Add Order
      </button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer Name</th>
            <th>Invoice Status</th>
            <th>Invoice Number</th>
            <th>Created By</th>
            <th>Created At</th>
            <th>Updated At</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.customer_name}</td>
              <td>{order.invoice_status}</td>
              <td>{order.invoice_number}</td>
              <td>{order.created_by}</td>
              <td>{order.created_at}</td>
              <td>{order.updated_at}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {showPopup && (
        <div className="popup-overlay">
          <div className="popup-container">
            <h3>Add New Order</h3>
            <form onSubmit={handleSubmit}>
              <label>
                Customer Name:
                <input
                  type="text"
                  name="customerName"
                  value={newOrder.customerName}
                  onChange={handleInputChange}
                  placeholder="Enter customer name"
                  required
                />
              </label>
              <label>
                Invoice Status:
                <select
                  name="invoiceStatus"
                  value={newOrder.invoiceStatus}
                  onChange={handleInputChange}
                  required
                >
                  <option value="Paid">Paid</option>
                  <option value="Unpaid">Unpaid</option>
                </select>
              </label>
              <label>
                Invoice Number:
                <input
                  type="text"
                  name="invoiceNumber"
                  value={newOrder.invoiceNumber}
                  onChange={handleInputChange}
                  placeholder="Enter invoice number"
                  required
                />
              </label>
              <label>
                Created By:
                <input
                  type="text"
                  name="createdBy"
                  value={newOrder.createdBy}
                  readOnly
                />
              </label>
              <div className="popup-buttons">
                <button type="submit" className="submit-button">
                  Submit
                </button>
                <button
                  type="button"
                  className="cancel-button"
                  onClick={() => setShowPopup(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Orders;
