import React, { useEffect, useState } from 'react';
import Sidebar from './Sidebar';
import './Dashboard.css'; // Add custom styling for the dashboard layout

const Dashboard = ({ onLogout }) => {
    const [analytics, setAnalytics] = useState({
        totalBillboards: 0,
        totalOrders: 0,
        totalReservations: 0,
        totalUsers: 0,
    });

    useEffect(() => {
        // Fetch analytics data from the backend
        fetch('http://localhost:3000/api/analytics')
            .then((res) => res.json())
            .then((data) => setAnalytics(data))
            .catch((err) => console.error('Error fetching analytics:', err));
    }, []);

    return (
        <div style={{ display: 'flex' }}>
            <Sidebar onLogout={onLogout} />
            <div className="dashboard-content">
                <h1>Dashboard</h1>
                <div className="analytics-cards">
                    <div className="analytics-card">
                        <h3>Total Billboards</h3>
                        <p>{analytics.totalBillboards}</p>
                    </div>
                    <div className="analytics-card">
                        <h3>Total Orders</h3>
                        <p>{analytics.totalOrders}</p>
                    </div>
                    <div className="analytics-card">
                        <h3>Total Reservations</h3>
                        <p>{analytics.totalReservations}</p>
                    </div>
                    <div className="analytics-card">
                        <h3>Total Users</h3>
                        <p>{analytics.totalUsers}</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
