import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const RealTimeData = ({ onLogout }) => {
    const [products, setProducts] = useState([]); // State to hold product data

    useEffect(() => {
        // Connect to the backend Socket.IO server
        const socket = io('http://localhost:3000'); // Ensure this URL points to your backend

        // Listen for 'stockUpdate' events from the backend
        socket.on('stockUpdate', (updatedProducts) => {
            console.log('Received stock update:', updatedProducts); // Debug log
            setProducts(updatedProducts); // Update state with product data
        });

        // Cleanup: Disconnect from the socket when the component unmounts
        return () => {
            socket.disconnect();
        };
    }, []); // Empty dependency array ensures this runs only once

    return (
        <div style={{ maxWidth: '600px', margin: '50px auto' }}>
            <h2>Real-Time Stock Data</h2>
            <button
                style={{
                    marginBottom: '20px',
                    padding: '10px 15px',
                    backgroundColor: '#f44336',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
                onClick={onLogout}
            >
                Logout
            </button>
            <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Product</th>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product) => (
                            <tr key={product.id}>
                                <td style={{ border: '1px solid black', padding: '8px' }}>{product.name}</td>
                                <td style={{ border: '1px solid black', padding: '8px' }}>{product.stock}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td style={{ border: '1px solid black', padding: '8px' }} colSpan="2">
                                No products available.
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default RealTimeData;
