import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import { useNavigate } from 'react-router-dom';

const RealTimeData = ({ onLogout }) => {
    const [products, setProducts] = useState([]); // State to hold product data
    const navigate = useNavigate();

    useEffect(() => {
        // Check if the token exists in localStorage
        const token = localStorage.getItem('token');
        if (!token) {
            // Redirect to login if no token is found
            navigate('/');
            return;
        }

        // Connect to the backend Socket.IO server with the token
        const socket = io('http://localhost:3000', {
            auth: { token }, // Pass the token for authentication
        });

        // Listen for 'stockUpdate' events from the backend
        socket.on('stockUpdate', (updatedProducts) => {
            console.log('Received stock update:', updatedProducts); // Debug log
            setProducts(updatedProducts); // Update state with product data
        });

        // Handle authentication errors from the server
        socket.on('connect_error', (err) => {
            console.error('Connection error:', err.message);
            onLogout(); // Log the user out if the token is invalid
        });

        // Cleanup: Disconnect from the socket when the component unmounts
        return () => {
            socket.disconnect();
        };
    }, [navigate, onLogout]); // Add dependencies for navigate and onLogout

    return (
        <div style={{ maxWidth: '600px', margin: '50px auto' }}>
            <h2>Real-Time Stock Data</h2>
            <button
                style={{
                    marginBottom: '20px',
                    padding: '10px 15px',
                    backgroundColor: '#f44336',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
                onClick={() => {
                    onLogout(); // Clear token and navigate to login
                }}
            >
                Logout
            </button>
            <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Product</th>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((product) => (
                            <tr key={product.id}>
                                <td style={{ border: '1px solid black', padding: '8px' }}>{product.name}</td>
                                <td style={{ border: '1px solid black', padding: '8px' }}>{product.stock}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td style={{ border: '1px solid black', padding: '8px' }} colSpan="2">
                                No products available.
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default RealTimeData;
