import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const RealTimeData = ({ onLogout }) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const socket = io('http://localhost:3000'); // Connect to backend
        socket.on('stockUpdate', (updatedProducts) => {
            setProducts(updatedProducts); // Update product data
        });

        return () => socket.disconnect(); // Disconnect when unmounting
    }, []);

    return (
        <div style={{ maxWidth: '600px', margin: '50px auto' }}>
            <h2>Real-Time Stock Data</h2>
            <button
                style={{
                    marginBottom: '20px',
                    padding: '10px 15px',
                    backgroundColor: '#f44336',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                }}
                onClick={onLogout}
            >
                Logout
            </button>
            <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
                <thead>
                    <tr>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Product</th>
                        <th style={{ border: '1px solid black', padding: '8px' }}>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.id}>
                            <td style={{ border: '1px solid black', padding: '8px' }}>{product.name}</td>
                            <td style={{ border: '1px solid black', padding: '8px' }}>{product.stock}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default RealTimeData;
