// Updated Orders.js
import React, { useState, useEffect } from 'react';
import './Orders.css';

const Orders = ({ socket, username }) => {
    const [orders, setOrders] = useState([]);
    const [showPopup, setShowPopup] = useState(false);
    const [newOrder, setNewOrder] = useState({
        customer_name: '',
        invoice_status: 'Paid', // Default dropdown value
        invoice_number: '',
        created_by: username || '', // Default to the logged-in username
    });

    useEffect(() => {
        // Fetch initial orders
        socket.emit('fetchOrders');

        // Listen for order updates
        socket.on('ordersUpdate', (updatedOrders) => {
            setOrders(updatedOrders);
        });

        return () => {
            socket.off('ordersUpdate');
        };
    }, [socket]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewOrder((prevOrder) => ({
            ...prevOrder,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        socket.emit('addOrder', newOrder);
        setShowPopup(false);
        setNewOrder({
            customer_name: '',
            invoice_status: 'Paid',
            invoice_number: '',
            created_by: username || '',
        });
    };

    return (
        <div className="orders-container">
            <h1>Orders</h1>
            <button className="add-order-button" onClick={() => setShowPopup(true)}>Add Order</button>

            <table className="orders-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Customer Name</th>
                        <th>Invoice Status</th>
                        <th>Invoice Number</th>
                        <th>Created By</th>
                        <th>Created At</th>
                        <th>Updated At</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order) => (
                        <tr key={order.id}>
                            <td>{order.id}</td>
                            <td>{order.customer_name}</td>
                            <td>{order.invoice_status}</td>
                            <td>{order.invoice_number}</td>
                            <td>{order.created_by}</td>
                            <td>{new Date(order.created_at).toLocaleString()}</td>
                            <td>{new Date(order.updated_at).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {showPopup && (
                <div className="popup-overlay">
                    <div className="popup-container">
                        <h2>Add New Order</h2>
                        <form onSubmit={handleSubmit}>
                            <label>Customer Name:</label>
                            <input
                                type="text"
                                name="customer_name"
                                value={newOrder.customer_name}
                                onChange={handleInputChange}
                                placeholder="Enter customer name"
                                required
                            />
                            <label>Invoice Status:</label>
                            <select
                                name="invoice_status"
                                value={newOrder.invoice_status}
                                onChange={handleInputChange}
                            >
                                <option value="Paid">Paid</option>
                                <option value="Unpaid">Unpaid</option>
                            </select>
                            <label>Invoice Number:</label>
                            <input
                                type="text"
                                name="invoice_number"
                                value={newOrder.invoice_number}
                                onChange={handleInputChange}
                                placeholder="Enter invoice number"
                                required
                            />
                            <label>Created By:</label>
                            <input
                                type="text"
                                name="created_by"
                                value={newOrder.created_by}
                                readOnly
                            />
                            <div className="popup-buttons">
                                <button type="submit" className="submit-button">Submit</button>
                                <button
                                    type="button"
                                    className="cancel-button"
                                    onClick={() => setShowPopup(false)}
                                >
                                    Cancel
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Orders;
