import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Billboards from './components/Billboards';
import Orders from './components/Orders';
import Reservations from './components/Reservations';

const App = () => {
    const [token, setToken] = useState(null);

    useEffect(() => {
        // Check for a token in localStorage when the app loads
        const savedToken = localStorage.getItem('token');
        if (savedToken) {
            setToken(savedToken);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token'); // Remove the token
        setToken(null); // Reset token state
    };

    return (
        <Router>
            <Routes>
                {/* If user is logged in, allow access to app routes */}
                {token ? (
                    <>
                        <Route path="/" element={<Navigate to="/dashboard" />} />
                        <Route path="/dashboard" element={<Dashboard onLogout={handleLogout} />} />
                        <Route path="/billboards" element={<Billboards onLogout={handleLogout} />} />
                        <Route path="/orders" element={<Orders onLogout={handleLogout} />} />
                        <Route path="/reservations" element={<Reservations onLogout={handleLogout} />} />
                    </>
                ) : (
                    // Otherwise, redirect to login
                    <>
                        <Route path="*" element={<Navigate to="/login" />} />
                        <Route path="/login" element={<Login setToken={setToken} />} />
                    </>
                )}
            </Routes>
        </Router>
    );
};

export default App;
