import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import Navbar from './Navbar';

const RealTimeData = ({ onLogout }) => {
    const [billboards, setBillboards] = useState([]); // State to hold billboard data

    useEffect(() => {
        // Connect to the backend Socket.IO server
        const socket = io('http://localhost:3000');

        // Listen for 'billboardUpdate' events from the backend
        socket.on('billboardUpdate', (data) => {
            console.log('Billboards updated:', data);
            setBillboards(data);
        });

        // Fetch initial data
        fetch('http://localhost:3000/api/billboards')
            .then((response) => response.json())
            .then((data) => setBillboards(data))
            .catch((err) => console.error('Error fetching billboards:', err));

        // Cleanup: Disconnect from the socket when the component unmounts
        return () => {
            socket.disconnect();
        };
    }, []);

    return (
        <div>
            <Navbar onLogout={onLogout} />
            <div style={{ maxWidth: '1200px', margin: '50px auto' }}>
                <h2>Real-Time Billboard Data</h2>
                <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
                    <thead>
                        <tr>
                            <th style={{ border: '1px solid black', padding: '8px' }}>ID</th>
                            <th style={{ border: '1px solid black', padding: '8px' }}>Name</th>
                            <th style={{ border: '1px solid black', padding: '8px' }}>City</th>
                            <th style={{ border: '1px solid black', padding: '8px' }}>Type</th>
                            <th style={{ border: '1px solid black', padding: '8px' }}>Monthly Price</th>
                            <th style={{ border: '1px solid black', padding: '8px' }}>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {billboards.length > 0 ? (
                            billboards.map((billboard) => (
                                <tr key={billboard.id}>
                                    <td style={{ border: '1px solid black', padding: '8px' }}>{billboard.id}</td>
                                    <td style={{ border: '1px solid black', padding: '8px' }}>{billboard.name}</td>
                                    <td style={{ border: '1px solid black', padding: '8px' }}>{billboard.city}</td>
                                    <td style={{ border: '1px solid black', padding: '8px' }}>{billboard.type}</td>
                                    <td style={{ border: '1px solid black', padding: '8px' }}>{billboard.price_monthly} €</td>
                                    <td style={{ border: '1px solid black', padding: '8px' }}>{billboard.status}</td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td style={{ border: '1px solid black', padding: '8px' }} colSpan="6">
                                    No billboards available.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default RealTimeData;
