const RealTimeData = ({ onLogout, initialProducts }) => {
    const [products, setProducts] = useState(initialProducts);

    useEffect(() => {
        const socket = io('http://localhost:3000');

        socket.on('stockUpdate', (updatedProducts) => {
            setProducts(updatedProducts);
        });

        return () => {
            socket.disconnect();
        };
    }, []);

    return (
        <div>
            <h2>Real-Time Stock Data</h2>
            <button onClick={onLogout}>Logout</button>
            <table>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Stock</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.id}>
                            <td>{product.name}</td>
                            <td>{product.stock}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};
