import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import './Orders.css';

const Orders = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        // Establish socket connection
        const socket = io('http://localhost:3000'); // Replace with your backend URL if different

        // Authenticate the socket connection
        socket.auth = { token: localStorage.getItem('token') };

        socket.connect();

        console.log('Socket connected:', socket); // Debug: Ensure the socket connects

        // Listen for 'ordersUpdate' events
        socket.on('ordersUpdate', (updatedOrders) => {
            console.log('Received orders update:', updatedOrders); // Debug: Log received updates
            setOrders(updatedOrders);
        });

        // Cleanup on component unmount
        return () => {
            socket.disconnect();
            console.log('Socket disconnected'); // Debug: Ensure socket disconnects
        };
    }, []);

    return (
        <div className="orders-container">
            <h2>Orders</h2>
            <table className="orders-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Customer Name</th>
                        <th>Invoice Status</th>
                        <th>Invoice Number</th>
                        <th>Created By</th>
                        <th>Created At</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.length > 0 ? (
                        orders.map((order) => (
                            <tr key={order.id}>
                                <td>{order.id}</td>
                                <td>{order.customer_name}</td>
                                <td>{order.invoice_status}</td>
                                <td>{order.invoice_number}</td>
                                <td>{order.created_by}</td>
                                <td>{order.created_at}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6">No orders available.</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default Orders;
